mapper.select:
SELECT K.*,b1.EnumText AS xmlxmc,b2.EnumText AS levelmc FROM ( SELECT RS.id,RS.khxmid,RS.dwid,KM.khxm,KM.xmlx,KM.totalscore,BS.unitShortName,BS.unitLevel, sum(sc.scroe) as score FROM wmb_sbnr RS LEFT JOIN wmb_khxm KM ON RS.khxmid=KM.id LEFT JOIN BASE_UNIT BS ON RS.dwid=BS.id LEFT JOIN wmb_score sc ON sc.sbnrid = RS.id where RS.dwid in (select id from BASE_UNIT where parentUnitCode = #{dwid})
and KM.xmlx = #{xmlx}
and BS.unitLevel = #{unitLevel}
group by RS.id )K,BASE_ENUM b1,BASE_ENUM b2 where K.xmlx=b1.EnumCode and b1.EnumType='assessmentType' AND K.unitLevel=b2.EnumCode and b2.EnumType='unitLevel'
